mapper.insert:
INSERT INTO cmm_code(
    cd, div_cd, parent_cd, cd_nm, sort, description, use_yn, etc1, etc2, etc3, etc4, created_by, created_at, updated_by, updated_at, client_id
) VALUES (
    #{cd}, #{divCd}, #{parentCd}, #{cdNm}, #{sort}, #{description}, #{useYn}, #{etc1}, #{etc2}, #{etc3}, #{etc4}, #{createdBy}, #{createdAt}, #{createdBy}, #{createdAt}, #{clientId}
)

mapper.delete:
DELETE FROM cmm_code
WHERE client_id = #{clientId}
  AND div_cd = #{divCd}

mapper.insert:
INSERT INTO cmm_code_div (
    div_cd, div_nm, div_service, upd_enable_yn, client_id, user_define_col
) VALUES (
    #{divCd}, #{divNm}, #{divService}, #{updEnableYn}, #{clientId}, #{userDefineCol}
)

mapper.update:
UPDATE cmm_code_div SET
   div_cd = #{divCd},
   div_nm = #{divNm},
   div_service = #{divService},
   upd_enable_yn = #{updEnableYn},
   client_id = #{clientId},
   user_define_col = #{userDefineCol}
WHERE client_id = #{clientId}
  AND div_cd = #{divCd}

mapper.delete:
DELETE
FROM cmm_code_div
WHERE client_id = #{clientId}
  AND div_cd = #{divCd}
