mapper.select:
SELECT a.*, e.name, l.username FROM cmm_action_log a LEFT OUTER JOIN client_user e ON a.user_id = e.id LEFT OUTER JOIN client_user_login l ON a.user_id = l.user_id
AND a.createdAt BETWEEN #{actionLogRequestDto.startedAt} and #{actionLogRequestDto.endedAt}
AND a.client_id = #{actionLogRequestDto.clientId}
AND a.div_cd = #{actionLogRequestDto.divCd}
AND e.name LIKE CONCAT(CONCAT('%',#{actionLogRequestDto.empNm}),'%')
AND l.username LIKE CONCAT(CONCAT('%',#{actionLogRequestDto.userId}),'%')
AND a.ip LIKE CONCAT(CONCAT('%',#{actionLogRequestDto.ip}),'%')

mapper.insert:
INSERT INTO cmm_action_log(id,
    user_id, ip, created_at, menu, sub_menu, action, action_url, input_val, language , etc1, etc2, etc3, etc4, etc5, div_cd, client_id
)VALUES(,
    #{userId}, #{ip}, #{createdAt}, #{menu}, #{subMenu}, #{action}, #{actionUrl}, #{inputVal}, #{language}, #{etc1}, #{etc2}, #{etc3}, #{etc4}, #{etc5}, #{divCd}, #{clientId}
)
