mapper.insert:
INSERT INTO client_user(
     name, nickname, phone, email
    , identification, extension_no, position_cd, duty_cd, sort, status
    , created_by, created_at, updated_by, updated_at, user_key, client_id, department_id
) VALUES (
    #{name}, #{nickname}, #{phone}, #{email}
    , #{identification}, #{extensionNo}, #{positionCd}, #{dutyCd}, #{sort}, #{status}
    , #{createdBy}, #{createdAt}, #{updatedBy}, #{updatedAt}, #{userKey}, #{clientId}, #{departmentId}
 )

mapper.update:
UPDATE client_user SET
    name = #{name}
    , nickname = #{nickname}
    , phone = #{phone}
    , email = #{email}
    , identification = #{identification}
    , extension_no = #{extensionNo}
    , position_cd = #{positionCd}
    , duty_cd = #{dutyCd}
    , sort  = #{sort}
    , status = #{status}
    , updated_by = #{updatedBy}
    , updated_at = #{updatedAt}
    , department_id = #{departmentId}
WHERE id = #{id}
  AND client_id= #{clientId}

mapper.delete:
UPDATE client_user SET
    status = 'INACTIVE'
WHERE id = #{id}
  AND client_id = #{clientId}

mapper.select:
SELECT
FROM cmm_emp e JOIN cmm_login l ON e.emp_no = l.emp_no LEFT OUTER JOIN cmm_dept dept ON e.dept_no = dept.dept_no LEFT OUTER JOIN cmm_code postn ON e.postn_cd = postn.code AND postn.client_id = e.client_id AND postn.div_cd = 'OR02' LEFT OUTER JOIN cmm_code duty ON e.duty_cd = duty.code AND duty.client_id = e.client_id AND duty.div_cd = 'OR01' WHERE e.client_id = #{clientId}
AND e.emp_no = #{empNo}
AND e.emp_no IN ( SELECT b.target_id as emp_no FROM cmm_group a JOIN cmm_group_mapping b on a.group_id = b.group_id WHERE a.client_id = #{clientId} AND a.code = #{code} AND b.target = 'E'
UNION ALL
SELECT emp_no FROM cmm_emp WHERE dept_no IN ( SELECT b.target_id FROM cmm_group a JOIN cmm_group_mapping b on a.group_id = b.group_id WHERE a.client_id = #{clientId} AND a.code = #{code} AND b.target = 'D' ) )
AND e.emp_nm = #{empNm}

mapper.insert:
INSERT INTO client_user_login(
    provider_type, username, password, password_err_count, password_updated_at, user_id
) VALUES(
    #{providerType}, #{username}, #{password}, 0 , now(),#{id}
)

INSERT INTO client_user_login_hist (
    user_id, login_at, last_ip_address, client_id
)VALUES(
    #{id}, #{lastLoginAt} ,#{lastIpAddress}, #{clientId}
)

mapper.update:
UPDATE client_user_login SET
    password_err_count = 0,
    last_login_at = #{lastLoginAt},
    last_ip_address = #{lastIpAddress}
WHERE user_id = #{id}

UPDATE client_user_login SET
    last_logout_at = #{lastLogoutAt}
WHERE user_id = #{id}

UPDATE client_user_login SET
    password_err_count = password_err_count + 1
WHERE username = #{username}

UPDATE client_user_login SET
    password = #{password},
    password_err_count = 0,
    password_updated_at = #{passwordUpdatedAt}
WHERE user_id = #{id}
