mapper.select:
SELECT
FROM tb_cmm_emp e JOIN tb_cmm_login l ON e.emp_no = l.emp_no LEFT OUTER JOIN tb_cmm_dept dept ON e.dept_no = dept.dept_no LEFT OUTER JOIN tb_cmm_code postn ON e.postn_cd = postn.cd AND postn.cmpny_no = e.cmpny_no AND postn.div_cd = 'OR02' LEFT OUTER JOIN tb_cmm_code duty ON e.duty_cd = duty.cd AND duty.cmpny_no = e.cmpny_no AND duty.div_cd = 'OR01' WHERE e.cmpny_no = #{cmpnyNo}
AND e.emp_no = #{empNo}
AND e.emp_no IN ( SELECT b.data_no as emp_no FROM tb_cmm_group a JOIN tb_cmm_group_map b on a.grp_no = b.grp_no WHERE a.cmpny_no = #{cmpnyNo} AND a.grp_cd = #{grpCd} AND b.data_gbn = 'E'
UNION ALL
SELECT emp_no FROM tb_cmm_emp WHERE dept_no IN ( SELECT b.data_no FROM tb_cmm_group a JOIN tb_cmm_group_map b on a.grp_no = b.grp_no WHERE a.cmpny_no = #{cmpnyNo} AND a.grp_cd = #{grpCd} AND b.data_gbn = 'D' ) )
AND e.emp_nm = #{empNm}

mapper.insert:

    SELECT sq_cmm_emp.NEXTVAL FROM DUAL


INSERT INTO tb_cmm_emp(emp_no,
